Script.script:
function parse_skillsets()
rime = rime or {}
	rime.skills = {}
	for _, set in ipairs(gmcp.Char.Skills.Groups) do
		local skills = string.format("Char.Skills.Get %s", yajl.to_string({ group = set.name }))
		sendGMCP(skills)
	end
	send("\n")
  --GUI.topRight_update()
end

function remove_player(event)
  table.remove(swarm.room, table.index_of(swarm.room, gmcp.Room.RemovePlayer))
end

function addMob(event)
  rime.pve.addMob_event()
end

function removeMob(event)
  rime.pve.removeMob_event()
end

